CREATE TABLE "public"."topics_users_comments_association" ("id" UUID NOT NULL DEFAULT gen_random_uuid(), "topics_pk" UUID NOT NULL, "users_pk" text NOT NULL, "comment" Text NOT NULL, "created_at" timestamptz NOT NULL DEFAULT now(), PRIMARY KEY ("id") , FOREIGN KEY ("topics_pk") REFERENCES "public"."topics"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("users_pk") REFERENCES "public"."users"("id") ON UPDATE restrict ON DELETE restrict);COMMENT ON TABLE "public"."topics_users_comments_association" IS E'Contains comments attached to users and topics';
